(491, 46, 'exp_templates', 'template_data', 1552262968, 2, '{layout=\"_layouts/_layout_login\"}\n\n{!-- Meta Variables --}\n{layout:set name=\"meta_title\"}{exp:onmeapp_wallet:lang key=\"sign_in\"} | {site_name}{/layout:set}\n\n{!-- Styles & Scripts --}\n{layout:set name=\"extra_js_footer\"}\n<script>\n  $(document).ready(function() {\n	 {if last_segment == \"username_sent\"}notifyUsernameSent();{/if}\n	 {if last_segment == \"reset_success\"}notifyResetSuccess();{/if}\n	 {if last_segment == \"deleted\"}notifyDeleted();{/if}\n  });\n  \n  var notifyUsernameSent = function() {\n	$.notification({\n		content: \"{exp:onmeapp_wallet:lang key=\"username_sent\"}\",\n		icon: \'fas fa-exclamation\',\n		iconClass: \'bg-gradient-green text-white\',\n		autoclose: true,\n		autocloseTime: 20000,\n		inverseMode: true\n	});\n  };\n  \n  var notifyResetSuccess = function() {\n	$.notification({\n		content: \"{exp:onmeapp_wallet:lang key=\"reset_password_success\"}\",\n		icon: \'fas fa-exclamation\',\n		iconClass: \'bg-gradient-green text-white\',\n		autoclose: true,\n		autocloseTime: 20000,\n		inverseMode: true\n	});\n  };\n  \n  var notifyDeleted = function() {\n	$.notification({\n		content: \"{exp:onmeapp_wallet:lang key=\"user_deleted\"}\",\n		icon: \'fas fa-exclamation\',\n		iconClass: \'bg-gradient-green text-white\',\n		autoclose: true,\n		autocloseTime: 20000,\n		inverseMode: true\n	});\n  };\n</script>\n\n{exp:authenticate:login_form}\n<script>\n  var notifyError = function() {\n	$.notification({\n		title: \"{exp:onmeapp_wallet:lang key=\"error\"}\",\n		content: \"{global_errors}{error}{/global_errors}\",\n		icon: \'fas fa-times\',\n		iconClass: \'bg-gradient-pink text-white\',\n		autoclose: false,\n		btn: true,\n		btnText: \"{exp:onmeapp_wallet:lang key=\"support\"}\",\n		btnUrl: \"/support\"\n	});\n  };\n  \n  $(document).ready(function() {\n	 {if total_global_errors > 0}notifyError();{/if}\n  });\n</script>\n{/exp:authenticate:login_form}\n{/layout:set}\n\n{!-- Layout Variables --}\n{layout:set name=\"title\"}{exp:onmeapp_wallet:lang key=\"sign_in\"}{/layout:set}\n{layout:set name=\"sub_title\"}{exp:onmeapp_wallet:lang key=\"sign_in_desc\"}{/layout:set}\n{layout:set name=\"background\"}{images_v}/img/login-cover.jpg{/layout:set}\n\n{!-- Content --}				\n				<!-- Social Logins -->\n				{exp:smart_members:social_login attr:class=\"mb-3\" secure_action=\"yes\" secure_return=\"yes\" popup=\"no\"}\n				<input type=\"hidden\" name=\"providers\" id=\"providers\" value=\"\" />\n				{providers}\n				<a href=\"#\" class=\"no-scroll mr-2\" data-toggle=\"tooltip\" title=\"{exp:onmeapp_wallet:lang key=\"login_with\"} {provider_label}\" data-social-login=\"{provider_name}\"><i class=\"fab fa-2x fa-{provider_name} fa-fw\"></i></a>\n				{/providers}\n				{/exp:smart_members:social_login}\n				\n				<!-- BEGIN login-form -->\n				{exp:authenticate:login_form class=\"validator\" name=\"login_form\"}				\n					<div class=\"form-group{if field_error:username} has-error{/if}\">\n						<label>{exp:onmeapp_wallet:lang key=\"email\"} {required_v}</label>\n						<input type=\"text\" class=\"form-control\" id=\"username\" name=\"username\" autofocus required autocomplete=\"off\" />\n						<span class=\"help-block with-errors\">{if field_error:username}{exp:stringer}{field_error:username}{/exp:stringer}{/if}</span>\n					</div>\n					<div class=\"form-group{if field_error:password} has-error{/if}\">\n						<label>{exp:onmeapp_wallet:lang key=\"password\"} {required_v}</label>\n						<div class=\"input-group\">\n							<input type=\"password\" class=\"form-control\" id=\"password_login\" name=\"password\" required />\n							<div class=\"input-group-append\">\n								<button class=\"btn btn-outline-secondary password-reveal rippler rippler-default text-center\" type=\"button\" tabindex=\"25\"><i class=\"far fa-fw fa-eye password-show text-white\"></i><i class=\"far fa-eye-slash text-white password-hide hidden\"></i></button>\n							</div>\n						</div>\n						<span class=\"help-block with-errors\">{if field_error:password}{exp:stringer}{field_error:password}{/exp:stringer}{/if}</span>\n					</div>\n					{if auto_login}\n					<input type=\"hidden\" name=\"auto_login\" value=\"1\" />\n					{/if}\n					<div class=\"d-flex align-items-center\">\n						<button type=\"submit\" class=\"btn btn-primary width-150 btn-rounded rippler rippler-default\">{exp:onmeapp_wallet:lang key=\"sign_in\"}</button>\n						<a href=\"/account/forgot\" class=\"m-l-10\">{exp:onmeapp_wallet:lang key=\"forgot_password\"}</a>\n					</div>\n				{/exp:authenticate:login_form}\n				<!-- END login-form -->\n				<div class=\"login-desc row m-t-30\">\n					<div class=\"col-md-6\">\n						<a href=\"/account/register\">{exp:onmeapp_wallet:lang key=\"register_here\"}</a>\n					</div>\n					<div class=\"col-md-6\">\n						{exp:channel:entries channel=\"custom_pages\" orderby=\"date\" sort=\"desc\" category=\"5\" dynamic=\"no\" disable=\"custom_fields|categories|category_fields|member_data|pagination\"}\n						<a href=\"/{url_title}\" class=\"btn btn-sm btn-silver rippler rippler-default btn-block no-text-decoration\">{title}</a>\n						{/exp:channel:entries}\n					</div>\n				</div>'),
