(499, 60, 'exp_templates', 'template_data', 1552263306, 2, '{layout=\"_layouts/_layout_main\"}\n\n{!-- Meta Variables --}\n{layout:set name=\"meta_title\"}{exp:onmeapp_wallet:lang key=\"add_funds\"}{/layout:set}\n\n{!-- Styles & Scripts --}\n{layout:set name=\"extra_js_footer\"}\n<script>\n  $(document).ready(function() {\n  	$(\'#amount\').keyup(function() {\n		var amount = $(\'#amount\').val();\n		var conv = $(\'input[name=conversion]\').val();\n		var onmebucks = parseFloat(Math.round((amount * conv) * 100) / 100).toFixed(2);\n		$(\'.onme-amount\').html(onmebucks);\n	});\n	{if last_segment == \"error\"}notifyError();{/if}\n  });\n  \n  var notifyError = function() {\n	$.notification({\n		title: \"{exp:onmeapp_wallet:lang key=\"error\"}\",\n		content: \"{exp:onmeapp_wallet:lang key=\"add_funds_error\"}\",\n		icon: \'fas fa-times\',\n		iconClass: \'bg-gradient-pink text-white\',\n		autoclose: false,\n		btn: true,\n		btnText: \"{exp:onmeapp_wallet:lang key=\"support\"}\",\n		btnUrl: \"/support\"\n	});\n  };\n</script>\n{/layout:set}\n\n{!-- Layout Variables --}\n{layout:set name=\"title\"}{exp:onmeapp_wallet:lang key=\"add_funds\"}{/layout:set}\n{layout:set:append name=\"breadcrumbs\"}{exp:onmeapp_wallet:lang key=\"add_funds\"}{/layout:set:append}\n\n{!-- Content --}\n			<div class=\"row\">\n			  <div class=\"col-12\">\n				<!-- BEGIN card -->\n				<div class=\"card w-100\">\n				  <div class=\"card-body\">\n				    {exp:onmeapp_wallet:add_funds_form class=\"validator\" return=\"/add_success\" error_return=\"/add-funds/error\"}\n					<div class=\"row\">\n					  <div class=\"form-group col-12\">\n						<label class=\"control-label\">{exp:onmeapp_wallet:lang key=\"payment_method\"} {required_v}</label>\n						<div class=\"input-group\">\n							<select name=\"payment_method\" id=\"payment_method\" class=\"form-control\" required>\n							  {embed=\"add-funds/_payment_methods\"}\n							</select>\n							<div class=\"input-group-append\">\n								<a href=\"/payment-met