(508, 51, 'exp_templates', 'template_data', 1552399589, 2, '{layout=\"_layouts/_layout_login\"}\n\n{if logged_in}{redirect=\"/\"}{/if}\n\n{!-- Meta Variables --}\n{layout:set name=\"meta_title\"}{exp:onmeapp_wallet:lang key=\"sign_up\"} | {site_name}{/layout:set}\n\n{!-- Styles & Scripts --}\n{layout:set name=\"extra_js_footer\"}\n<script>\n	$(document).ready(function() {	\n		var options = {};\n		options.ui = {\n		  container: \"#pwdstrength-container\",\n		  showVerdictsInsideProgressBar: true,\n		  viewports: {\n			progress: \".pwstrength_viewport_progress\"\n		  },\n		  progressExtraCssClasses: \"progress-sm\"\n		};\n		options.common = {\n		  debug: true,\n		  onLoad: function () {\n			$(\'#messages\').text(\'{exp:onmeapp_wallet:lang key=\"start_typing_password\"}\');\n		  }\n		};\n		$(\'#password\').pwstrength(options);\n	});\n</script>\n{/layout:set}\n\n{!-- Layout Variables --}\n{layout:set name=\"title\"}{exp:onmeapp_wallet:lang key=\"sign_up\"}{/layout:set}\n{layout:set name=\"sub_title\"}{exp:onmeapp_wallet:lang key=\"sign_up_desc\"}{/layout:set}\n{layout:set name=\"background\"}{images_v}/img/register-cover.jpg{/layout:set}\n\n{!-- Content --}\n				<!-- BEGIN register-form -->\n				{exp:user:register return=\"/add-funds/register_success\" form:class=\"validator\" required=\"username|first_name|last_name|password|password_confirm\"}\n					<!-- BEGIN row -->\n					<div class=\"row row-space-20\">\n                      <div class=\"form-group col-md-6\">\n                        <label for=\"first_name\">{exp:onmeapp_wallet:lang key=\"first_name\"} {required_v}</label>\n                        <input type=\"text\" class=\"form-control\" id=\"first_name\" name=\"first_name\" required autofocus />\n                        <span class=\"help-block with-errors\"></span>\n                      </div>\n\n                      <div class=\"form-group col-md-6\">\n                        <label for=\"last_name\">{exp:onmeapp_wallet:lang key=\"last_name\"} {required_v}</label>\n                        <input type=\"text\" class=\"form-control\" id=\"last_name\" name=\"last_name\" required />\n                        <span class=\"help-block with-errors\"></span>\n                      </div>\n                    </div>\n\n                    <div class=\"row\">\n                      <div class=\"form-group col-md-6\">\n                        <label for=\"username\">{exp:onmeapp_wallet:lang key=\"email\"} {required_v}</label>\n                        <input type=\"email\" class=\"form-control\" id=\"username\" name=\"username\"  data-minlength=\"3\" data-remote=\"/account/field\" data-remote-error=\"{exp:onmeapp_wallet:lang key=\"already_taken\"}\" required />\n                        <span class=\"help-block with-errors\"></span>\n                      </div>\n                      \n                      <div class=\"form-group col-md-6\">\n                        <label for=\"phone\">{exp:onmeapp_wallet:lang key=\"phone\"} {required_v}</label>\n                        <input type=\"tel\" class=\"form-control\" id=\"phone\" name=\"phone\" class=\"phone\" value=\"\" required />\n                        <span class=\"help-block with-errors\"></span>\n                      </div>\n                    </div>\n                    \n                    <div class=\"row\">\n                      <div class=\"form-group col-md-12\">\n                        <label for=\"company\">{exp:onmeapp_wallet:lang key=\"company\"}</label>\n                        <input type=\"text\" class=\"form-control\" id=\"company\" name=\"company\" value=\"\" />\n                        <span class=\"help-block with-errors\"></span>\n                      </div>\n                    </div>\n                    \n                    <div class=\"row\">\n                      <div class=\"col-md-12\">\n                        <div class=\"form-group\">\n                          <label for=\"address\">{exp:onmeapp_wallet:lang key=\"address\"} {required_v}