group-append\">\n								<button class=\"btn btn-outline-secondary rippler rippler-default password-reveal text-center\" type=\"button\" tabindex=\"25\"><i class=\"far fa-fw fa-eye password-show text-white\"></i><i class=\"far fa-eye-slash text-white password-hide hidden\"></i></button>\n							</div>\n                        </div>\n                        <span class=\"help-block with-errors\"></span>\n                      </div>\n                    </div>\n\n					<div class=\"mb-sm\">\n						<div class=\"checkbox-inline form-group\">\n                        	<input type=\"checkbox\" class=\"form_control\" name=\"accept_terms\" id=\"accept_terms\" value=\"y\" required />\n							<label for=\"accept_terms\">{exp:onmeapp_wallet:lang key=\"accept_terms\"}</label>\n                            <span class=\"help-block with-errors\"></span>\n						</div>\n					</div>\n                    <div class=\"row\">\n                      <div class=\"col-md-12 mb-sm\">\n                        {captcha}\n                      </div>\n                    </div>\n					<div class=\"d-flex align-items-center\">\n						<button type=\"submit\" class=\"btn btn-primary rippler rippler-default width-200 btn-rounded\">{exp:onmeapp_wallet:lang key=\"sign_up\"}</button>\n						<span class=\"m-l-20 text-white-transparent-5\">\n							{exp:onmeapp_wallet:lang key=\"already_have_account\"} &nbsp;\n							<a href=\"/\">{exp:onmeapp_wallet:lang key=\"sign_in\"}</a>\n						</span>\n					</div>\n				{/exp:user:register}\n				<!-- END register-form -->'),
