(584, 71, 'exp_templates', 'template_data', 1615756313, 2, '{layout=\"_layouts/_layout_main\"}\n\n{!-- Meta Variables --}\n{layout:set name=\"page_schema\"}ProfilePage{/layout:set}\n{layout:set name=\"meta_robots\"}noindex,nofollow{/layout:set}\n{layout:set name=\"meta_page_title\"}{layout:set name=\"meta_title\"}{exp:onmeapp_wallet:lang key=\"add_funds\"} - {exp:onmeapp_wallet:lang key=\"apple_pay\"}{/layout:set}\n\n{exp:onmeapp_wallet:applepay_checkout_form id=\"applepay_form\" submit_return=\"{current_url}\" error_return=\"CANCEL_RETURN/error\"}\n{!-- Styles & Scripts --}\n{layout:set name=\"extra_js_footer\"}\n<script src=\"https://js.stripe.com/v3/\"></script>\n\n<script>\n  var stripe = Stripe(\'{exp:onmeapp_wallet:stripe_public_key}\', {\n	apiVersion: \"2019-02-19\",\n  });\n  var elements = stripe.elements();\n  \n  var notifyError = function() {\n	$.notification({\n		title: \"{exp:onmeapp_wallet:lang key=\"error\"}\",\n		content: \"{exp:onmeapp_wallet:lang key=\"applepay_error\"}\",\n		icon: \'fas fa-times\',\n		iconClass: \'bg-gradient-pink text-white\',\n		autoclose: false,\n		btn: true,\n		btnText: \"{exp:onmeapp_wallet:lang key=\"support\"}\",\n		btnUrl: \"/support\"\n	});\n  };\n  \n  {if error}\n  var notifyOtherError = function() {\n	$.notification({\n		title: \"{exp:onmeapp_wallet:lang key=\"error\"}\",\n		content: \"{error}\",\n		icon: \'fas fa-times\',\n		iconClass: \'bg-gradient-pink text-white\',\n		autoclose: false,\n		btn: true,\n		btnText: \"{exp:onmeapp_wallet:lang key=\"support\"}\",\n		btnUrl: \"/support\"\n	});\n  };\n  \n  $(document).ready(function() {\n  	notifyOtherError();\n  });\n  {if:else}\n  var clientSecret = \'{client_secret}\';\n  \n  var paymentRequest = stripe.paymentRequest({\n	country: \'US\',\n	currency: \'usd\',\n	total: {\n	  label: \'{exp:onmeapp_wallet:lang key=\"onmeapp_wallet\"}\',\n	  amount: {amount_pennies},\n	},\n	requestPayerName: false,\n	requestPayerEmail: false,\n  });\n  \n  $(document).ready(function() {\n  	var prButton = elements.create(\'paymentRequestButton\', {\n	  paymentRequest: paymentRequest,\n	});\n\n	// Check the availability of the Payment Request API first.\n	paymentRequest.canMakePayment().then(function(result) {\n	  if (result) {\n		prButton.mount(\'#applepay-payment-request-button\');\n	  } else {\n		document.getElementById(\'applepay-payment-request-button\').style.display = \'none\';\n		notifyError();\n	  }\n	});\n	\n	paymentRequest.on(\'paymentmethod\', function(ev) {\n	  // Confirm the PaymentIntent without handling potential next actions (yet).\n	  stripe.confirmCardPayment(\n		clientSecret,\n		{payment_method: ev.paymentMethod.id},\n		{handleActions: false}\n	  ).then(function(confirmResult) {\n		if (confirmResult.error) {\n		  // Report to the browser that the payment failed, prompting it to\n		  // re-show the payment interface, or show an error message and close\n		  // the payment interface.\n		  ev.complete(\'fail\');\n		} else {\n		  // Report to the browser that the confirmation was successful, prompting\n		  // it to close the browser payment method collection interface.\n		  ev.complete(\'success\');\n		  // Check if the PaymentIntent requires any actions and if so let Stripe.js\n		  // handle the flow. If using an API version older than \"2019-02-11\" instead\n		  // instead check for: `paymentIntent.status === \"requires_source_action\"`.\n		  if (confirmResult.paymentIntent.status === \"requires_action\") {\n			// Let Stripe.js handle the rest of the payment flow.\n			stripe.confirmCardPayment(clientSecret).then(function(result) {\n			  if (result.error) {\n			  	//alert(\'error\');\n				window.location.href = \"{error_return}\";\n			  } else {\n			  	//alert(\'success\');\n				$(\'#applepay_form\').submit();\n			  }\n			});\n		  } else {\n		  	//alert(\'success\');\n			$(\'#applepay_form\').submit();\n		  }\n		}\n	  });\n	});\n  });\n  {/if}\n</script>\n{/layout:set}\n\n{!-- Layout Variables --}\n{layout:set name=\"title\"}{exp:onmeapp_wallet:lang key=\"add_funds\"}{/layout:set}\n{layout:set name=\"sub_title\"}{exp:onmeapp_wallet:lang key=\"apple_pay\"}{/layout:set}\n{layout:set:append name=\"breadcrumbs\"}{exp:onmeapp_wallet:lang key=\"add_funds\"}{/layout:set:append}\n{layout:set:append name=\"breadcrumbs_url\"}{if cancel_return}{cancel_return}{if:else}/{segment_1}{/if}{/layout:set:append}\n{layout:set:append name=\"breadcrumbs\"}{exp:onmeapp_wallet:lang key=\"apple_pay\"}{/layout:set:append}\n\n{!-- Contents --}\n<!-- BEGIN card -->\n					{exp:omg_cp:alert type=\"danger\" dismiss=\"no\" class=\"hidden\"}\n					{exp:onmeapp_wallet:lang key=\"applepay_error\"}\n					{/exp:omg_cp:alert}\n					\n					{if !error}\n					<div class=\"card text-left\">\n					  <div class=\"card-body p-0\">\n					    <div class=\"block-loading hidden d-flex align-items-center justify-content-center\" id=\"total_loading\"><i class=\"fas fa-circle-notch fa-spin fa-5x fa-fw text-white\"></i><span class=\"sr-only\">Loading...</span></div>\n						\n						<div class=\"table-responsive\">\n						  <table class=\"table m-b-0\">\n							<tbody>\n							  <tr>\n								<td><b>{exp:onmeapp_wallet:lang key=\"total_charge\"}</b></td>\n								<td class=\"text-right\">{exp:onmeapp_wallet:lang key=\"$\"}{amount}</td>\n							  </tr>\n							  <tr>\n								<td><b>{exp:onmeapp_wallet:lang key=\"onme_bucks\"}</b></td>\n								<td class=\"text-right\">{exp:onmeapp_wallet:lang key=\"$\"}{amount_onme}</td>\n							  </tr>\n							</tbody>\n						  </table>\n					  	</div>\n					  </div>\n					  <div class=\"card-footer d-block text-right\">\n						<span id=\"applepay-payment-request-button\"></span>\n						\n						<a href=\"{if cancel_return}{cancel_return}{if:else}/{segment_1}{/if}\" class=\"btn btn-block mt-3 btn-default btn-lg rippler rippler-inverse\">{exp:onmeapp_wallet:lang key=\"cancel\"}</a>\n					  </div>\n					</div>\n  					<!-- /.card -->\n					{/if}\n{/exp:onmeapp_wallet:applepay_checkout_form}'),
